"use strict";
{
    // Basic Data Type of TypeScript
    // String
    let firstName = "Mr. Omar Faruk";
    // Number
    let idNumber = 181151999;
    // Boolean
    let isStudent = true;
    // Undefined
    let x = undefined;
    // null
    let y = null;
    let d;
    d = 123;
    // Array
    let friends = ["Rajjak", "Kobori", "Bobita"];
    let friendAge = [70, 60, 65];
    // tuple --> array --> order --> type of values
    let coordinates = [1, 5];
    let ageName = [50, "Mr. Rajjak", true];
    ageName[0] = 55;
}
